                $data[$id] = array(
                    'title' => '',
                    'icons' => array(),
                );
                if (preg_match("'<h2.*>(.*)</h2>'imsU", $out[0][$i], $titleMatch)) {
                    $data[$id]['title'] = strip_tags($titleMatch[1]);
                }
                
                if (preg_match_all("'<i class=\"(.*)\"></i>'U", $out[0][$i], $iconsMatches)) {
                    $data[$id]['icons'] = $iconsMatches[1];
                }
            }
        }
        
        $this->data = serialize($data);
